class MarvelServices {
    _apiBase = 'https://gateway.marvel.com:443/v1/public/';
    _apiKey = 'apikey=f7044673c47662fab406d34b41749fc7';
    getResource = async (url) => {
        let res = await fetch(url);

        if (!res.ok) {
            throw new Error(`Could not fetch ${url}, status: ${res.status}`);
        }

        return await res.json();
    }  
    // request to API
    getAllCharacters = () => {
        return this.getResource(`${_apiBase}characters?limit=9&offset=210&${_apiKey}`)
    }
    // Get one character
    getCharacters = (id) => {
        return this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}?apikey=f7044673c47662fab406d34b41749fc7`)
    }
}

export default MarvelServices;
